import React from 'react';
import { Button } from '@themesberg/react-bootstrap';
import { TranslatableText } from '../../../components/translations';
import { permissionsKeys } from '../../../utils/permissionsKeys';
import PermissionChecker from '../../../components/permissionChecker';

export default function Header() { 

    const [show, setShow] = useState(false);
  
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  return (
      <>
    <Button hidden={!PermissionChecker(permissionsKeys.CATEGORIA_ADD)} type="button" onClick={handleShow}>
    <TranslatableText  entry={'Agregar'} />
  </Button>
  <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me.
          Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleClose}>
            Close
          </Button>
            
        </Modal.Footer>
      </Modal>
  </>
  );
};
