import React, { useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import toast from 'react-hot-toast';
import { Button, Modal } from '@themesberg/react-bootstrap';

import Manager from '../../components/manager';
import { TranslatableText } from '../../components/translations';
import { api as votos, useDeleteMutation, useGetQuery as useGetUserQuery } from '../../redux/apis/votos';
import withRedux from '../../redux/hoc/manager';

import Table from './table';
import Header from './header';
import { useState } from 'react';

const Votos = ({ source, onQueryChange }) => {
  const { Id } = useParams();
  const history = useHistory();
  const IdUser = JSON.parse(localStorage.getItem('usuario')).id
  const [remove, { isSuccess: isDeleteSuccess, data: deleteData, isError: isDeleteError, error: deleteError }] = useDeleteMutation();
  const [update, { isSuccess: isUpdateSuccess, data: updateData, isError: isUpdateError, error: updateError }] = useDeleteMutation();
  const [isactivo, setIsActivo] = useState(true);
  const { data: { VotadoEmpleadoId, VotanteEmpleadoId, FechaVoto, AprobadorEmpleadoId, FechaAprobado, CategoriaId, Motivo, Aprobado, MotivoRechazo } = {} } = useGetUserQuery(Id, { skip: !!!Id });

  const onSaveHandler = () => {
    if(history.location.pathname.includes('update')){
       update({VotadoEmpleadoId:VotadoEmpleadoId , VotanteEmpleadoId: VotanteEmpleadoId, FechaVoto: FechaVoto, AprobadorEmpleadoId: IdUser , FechaAprobado, CategoriaId: CategoriaId, Motivo: Motivo, Aprobado: true});
    }
    else{
        remove(Id);
    }
    
  };
  const onCancelHandler = () => {
    history.goBack();
  }

  useEffect(() => {
    if (isDeleteSuccess) {
      toast.success("Se rechazo voto con exito");
      history.goBack();
    }
    else if (isDeleteError) {
      const errors = [].concat(...Object.values(deleteError.data));
      toast.error(errors);
      history.goBack();
    }
  }, [isDeleteSuccess, deleteData, isDeleteError, deleteError]);

  useEffect(() => {
    if (isUpdateSuccess) {
      toast.success("Se aprobo voto con exito");
      history.goBack();
    }
    else if (isDeleteError) {
      const errors = [].concat(...Object.values(updateError.data));
      toast.error(errors);
      history.goBack();
    }
  }, [isUpdateSuccess, updateData, isUpdateError, updateError]);

  useEffect(() => {
    if(history.location.pathname.includes('update')){
      setIsActivo(false)
    }
  }, [history.location.pathname]);

  return (
    <>
      <Manager header={Header}  entry={'Votos a Controlar'} table={Table} source={source} onQueryChange={onQueryChange} />
      <Modal show={!!Id} onHide={onCancelHandler} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>
           {(isactivo) && <TranslatableText entry="Aprobar Voto" />}

           {(!isactivo) && <TranslatableText entry="Rechazar voto" />}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        {(isactivo) && <TranslatableText  entry="Confirmar aprobar voto" />}
        {(!isactivo) && <TranslatableText  entry="Confirmar rechazo del voto" />}
        </Modal.Body>
        <Modal.Footer>
          <Button type="button" variant="outline-primary" onClick={onCancelHandler}>
            <TranslatableText  entry="Cancelar" />
          </Button>
          <Button type="button" className="ms-2" onClick={onSaveHandler}>
          {(isactivo) && <TranslatableText  entry="Aprobar" />}          
          {(!isactivo) && <TranslatableText  entry="Rechazar" />}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default withRedux(votos)(Votos);
